    private void proxyButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proxyButtonActionPerformed
        // TODO add your handling code here:

        final JFileChooser fc = new JFileChooser();
        int returnVal = fc.showOpenDialog(this);
        FileSystemView a = fc.getFileSystemView();

        if (returnVal == JFileChooser.APPROVE_OPTION) {
            File file = fc.getSelectedFile();
            System.out.println("Selected file: " + file.getAbsolutePath());

            //file.canRead();
            System.out.println(fc.getFileSystemView());

            try {
                // What to do with the file, e.g. display it in a TextArea
                FileReader fileReader = new FileReader(file.getAbsolutePath());

                BufferedReader br = new BufferedReader(fileReader);

                String line;
                loggertextArea.append("\n Number Of Proxies ");
                while ((line = br.readLine()) != null) {
                    System.out.println(line);
                    proxyList.add(new ProxyImport(line));
                    loggertextArea.append("\n " + line);

                    countOfProxy++;

                }
                loggertextArea.append("\nNumber Of Count : " + countOfProxy);
                loggertextArea.append("\nPlease wait Checking Proxies ");

                searchButton.setEnabled(false);

                ArrayList<ProxyImport> proxyListClone = (ArrayList<ProxyImport>) proxyList.clone();
                ExecutorService executor = Executors.newFixedThreadPool(1);
                Callable worker = new CheckProxy();

                try {
                    executor.submit(worker);
                } catch (Exception ex) {
                    Logger.getLogger(MainPage.class.getName()).log(Level.SEVERE, null, ex);
                }

                
                System.out.println(file.getAbsolutePath());

                loggertextArea.append("\nProxy Loaded!!");

            } catch (Exception e) {
                loggertextArea.append("\n\nPlease Import a Valid .txt file!!");
                loggertextArea.append("\nProxy in the file must be in the following format!!");
                loggertextArea.append("\nProxyIP:ProxyPort:ProxyUsername:ProxyPassword");
            }
        } else {
            loggertextArea.append("\n\nPlease Import a Valid .txt file!!");
            loggertextArea.append("\nProxy in the file must be in the following format!!");
            loggertextArea.append("\nProxyIP:ProxyPort:ProxyUsername:ProxyPassword");
        }
    }//GEN-LAST:event_proxyButtonActionPerformed

    private void searchButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchButtonActionPerformed
        // TODO add your handling code here:
//executor = Executors.newFixedThreadPool(1);

        if (keywordTextField.getText().length() > 0) {
            keywordList.clear();
            String keyfieldvalue = keywordTextField.getText();
            String eachvalue[] = keyfieldvalue.split(",");
            for (String eachvalue1 : eachvalue) {
                keywordList.add(eachvalue1);
            }

        }
        System.out.println("proxyList:::" + proxyList.size());
        System.out.println("countOfProxy:::" + countOfProxy);
//        loggertextArea.append("\nNumber Of Proxies : " + countOfProxy);
        if (proxyList.size() > 0) {

            if (keywordList.size() > 0) {
                try {
                    if (countryList.getSelectedValuesList().isEmpty()) {
                        throw new Exception();
                    }
                    loggertextArea.append("\n process started please wait");
                    Callable worker = new MainThread(objMonsterScrapperDao, proxyList);

                    try {
                        executor.submit(worker);

                    } catch (Exception ex) {
                        Logger.getLogger(MainPage.class.getName()).log(Level.SEVERE, null, ex);
                    }

                    // im.start();
                    System.out.println("1");
                } catch (Exception e) {

                    JOptionPane.showMessageDialog(null, "Please Select Country");
                    proxyButton.setEnabled(true);
                    CSVButton.setEnabled(true);

                }

            } else {
                JOptionPane.showMessageDialog(null, "Please Select keyword");
                proxyButton.setEnabled(true);
                loadKeywordsutton.setEnabled(true);
                CSVButton.setEnabled(true);
            }

        } else {
            JOptionPane.showMessageDialog(null, "Please Select Valid Proxy");
            proxyButton.setEnabled(true);
            CSVButton.setEnabled(true);
        }
        proxyButton.setEnabled(false);
        loadKeywordsutton.setEnabled(false);
        CSVButton.setEnabled(false);
    }//GEN-LAST:event_searchButtonActionPerformed

