    private void loadKeywordsuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loadKeywordsuttonActionPerformed
        // TODO add your handling code here:
        keywordList.clear();
        final JFileChooser fc = new JFileChooser();
        int returnVal = fc.showOpenDialog(this);
        FileSystemView a = fc.getFileSystemView();

        if (returnVal == JFileChooser.APPROVE_OPTION) {
            File file = fc.getSelectedFile();
            System.out.println("Selected file: " + file.getAbsolutePath());

            //file.canRead();
            System.out.println(fc.getFileSystemView());

            try {
                // What to do with the file, e.g. display it in a TextArea
                FileReader fileReader = new FileReader(file.getAbsolutePath());

                BufferedReader br = new BufferedReader(fileReader);

                String line = "";
                loggertextArea.append("\n Multiple Keywords Loaded : ");
                while ((line = br.readLine()) != null) {
                    keywordList.add(line);
                    loggertextArea.append("\n " + line);
                }

            } catch (Exception e) {

            }
        }
    }//GEN-LAST:event_loadKeywordsuttonActionPerformed

